private void btnNuevoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNuevoActionPerformed
// TODO add your handling code here:
   
    int p=ap.busca(leeNumero());
       if(p!=-1)
           mensaje("Error:DNI Repetido");
       else{
 
           DNI nuevo = new DNI(leeNumero(),
             new Persona(leeNombres(), leeApellidos(), leeEdad(), leePeso()),
             new Fecha(leeDiaEmision(), leeMesEmision(), leeAñoEmision()), 
             new Fecha(leeDiaCaducidad(), leeMesCaducidad(), leeAñoCaducidad()) );
           
           
           lista(nuevo);
           ap.agrega(nuevo);
       }
         
}//GEN-LAST:event_btnNuevoActionPerformed

    private void btnBuscaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscaActionPerformed
        // TODO add your handling code here:
         int p=ap.busca(leeNumero());
       if(p==-1)
           mensaje("Error:Codigo no existe");
       else{
           DNI pp=ap.getDNI(p);
           lista(pp);
       }
        
        
    }//GEN-LAST:event_btnBuscaActionPerformed

    private void btnModificaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnModificaActionPerformed
        // TODO add your handling code here:
          int p=ap.busca(leeNumero());
       if(p==-1)
           mensaje("Error: DNI No encontrado");
       else{
           
          DNI nuevo = new DNI(leeNumero(),
             new Persona(leeNombres(), leeApellidos(), leeEdad(), leePeso()),
             new Fecha(leeDiaEmision(), leeMesEmision(), leeAñoEmision()), 
             new Fecha(leeDiaCaducidad(), leeMesCaducidad(), leeAñoCaducidad()) );
         
           ap.reemplaza(nuevo, p);
          
       }
        
    }//GEN-LAST:event_btnModificaActionPerformed

    private void btnEliminaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminaActionPerformed
        // TODO add your handling code here:
        
         int p=ap.busca(leeNumero());
       if(p==-1)
           mensaje("Error: DNI no existe");
       else{
           ap.elimina(p);
           mensaje("Eliminacion satisfactoria");
       }
        
    }//GEN-LAST:event_btnEliminaActionPerformed

