private String leeNumero(){return txtNumero.getText();}
//guarda variables en la clase PERSONA
private String leeNombres(){return txtNombres.getText();}
private String leeApellidos(){return txtApellidos.getText();}
private int leeEdad(){return Integer.parseInt(txtEdad.getText());}
private double leePeso(){return Double.parseDouble(txtPeso.getText());}
private int leeDiaEmision(){ return Integer.parseInt(txtDiaEmision.getText());} 
private int leeMesEmision(){ return cboMesEmision.getSelectedIndex();} 
private int leeAñoEmision(){ return Integer.parseInt(txtAñoEmision.getText());} 
private int leeDiaCaducidad(){ return Integer.parseInt(txtDiaCaducidad.getText());}
//guarda variables en la clase FECHA

private int leeMesCaducidad(){ return cboMesCaducidad.getSelectedIndex();} 
private int leeAñoCaducidad(){ return Integer.parseInt(txtAñoCaducidad.getText());} 

    public void listado(){
    txtSalida.setText("Numoro\tNombre\tApellido\tEdad\tPeso\tFecha Emision\tFecha Caducidad\n");
    for(int i=0;i<ap.getN();i++){
        DNI p=ap.getDNI(i);
        imprime(p.getNumero()+"\t"+p.getDueño().getNombre()+"\t"+p.getDueño().getApellido()+"\t"+ p.getDueño().getEdad()+"\t"+p.getDueño().getPeso()+"\t"+p.getEmision().getDia()+"/"+ p.getEmision().getMes()+"/"+ p.getEmision().getAño()+"\t"+p.getCaducidad().getDia()+"/"+ p.getCaducidad().getMes()+"/"+ p.getCaducidad().getAño());
    }
}

private void lista(DNI dni){ 
    imprime("Numero DNI\t\t: "+dni.getNumero()); 
    imprime("Nombres\t\t: "+dni.getDueño().getNombre()); 
    imprime("Apellidos\t\t: "+dni.getDueño().getApellido()); 
    imprime("Edad\t\t: "+dni.getDueño().getEdad()); 
    imprime("Peso\t\t: "+dni.getDueño().getPeso()); 
    imprime("Fecha Emision\t\t: "+dni.getEmision().getDia()+"/"+ dni.getEmision().getMes()+"/"+ dni.getEmision().getAño()); 
    imprime("Fecha Caducidad\t: "+dni.getCaducidad().getDia()+"/"+ dni.getCaducidad().getMes()+"/"+ dni.getCaducidad().getAño()); 
    imprime("--------------------------------------------------"); } 

private void imprime(String s){ txtSalida.append(s+"\n"); }

public void mensaje(String s){
    JOptionPane.showMessageDialog(this, s);//ctrl+barra espaciadora pa q busque JOptionPane
}


